mapper.select:
select * from inn_goods

	city_code=#{cityCode}
	
		and product_id =#{productId}
	
	
		and goods_title like '%${goodsTitle}%'
	
	
		and DATE_FORMAT(up_time,'%Y-%m-%d')>=#{beginTime}
	
	
		and DATE_FORMAT(up_time,'%Y-%m-%d')<=#{endTime}
	

order by up_time desc limit #{beginResult},#{pageSize}

select count(1) from inn_goods

	city_code=#{cityCode}
	
		and product_id =#{productId}
	
	
		and q.title like '%${title}%'
	
	
		and DATE_FORMAT(up_time,'%Y-%m-%d')>=#{beginTime}
	
	
		and DATE_FORMAT(up_time,'%Y-%m-%d')<=#{endTime}

